async function init() {
    let BASEAPI = "https://8080-rplsaci-pemiluapi-jfvwph8s12t.ws-us116.gitpod.io";
    // Ambil data login dari localStorage
    const data = JSON.parse(localStorage.getItem("loginObject"));
    console.log(data);
    let nama;
    // Cek apakah data login tersedia
    if (!data || !localStorage.getItem("loginObject")) {
        alert("NO USERDATA, REDIRECTING TO LOGIN");
        document.location.href = "./index.html";
        return; // Hentikan eksekusi lebih lanjut jika data tidak ada
    }
    try {
        // Lakukan permintaan POST untuk memverifikasi pengguna
        const response = await fetch(BASEAPI + "/api/user/login", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                username: data.username,
                password: data.password
            })
        });
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        const result = await response.json();
        if (result.error) {
            // Tangani kesalahan jika ada
            console.error("Login failed: ", result.error);
            alert("Login failed. Please try again.");
        } else // Tampilkan nama pengguna jika login berhasil
        nama = result.user.username;
    } catch (error) {
        console.error("An error occurred while logging in", error);
        alert("An error occurred while logging in. Please try again.");
    }
    let pilihan = JSON.parse(localStorage.getItem("Pilihan"));
    let { MPK, OSIS } = pilihan;
    // let selectedMPK = pemilihan.MPK[MPK];
    // let selectedOSIS = pemilihan.OSIS[OSIS];
    document.querySelector("#confirmationMessage").innerHTML = `Saya <strong>${nama}</strong> Menyatakan Akan Memilih Kandidat Ketua MPK Nomor urut ${MPK} dan Kandidat Ketua OSIS Nomor urut ${OSIS}`;
    document.querySelector(`#mpk${MPK}`).classList.remove("hidden");
    document.querySelector(`#osis${OSIS}`).classList.remove("hidden");
// document.querySelector("#selectedCandidates").innerHTML = `
// `;
}
